mapper.select:
SELECT p.*, (SELECT AVG(REVSTAR) FROM REVIEW r WHERE r.PRDNO = p.PRDNO) AS avgRating, (SELECT COUNT(*) FROM REVIEW r WHERE r.PRDNO = p.PRDNO) AS reviewCount, ( SELECT COUNT(*) FROM REVIEW r INNER JOIN USERS u ON u.userNo = r.userNo INNER JOIN REVIEW_FEEDBACK rf ON rf.revNo = r.revNo WHERE r.prdNo = p.prdNo AND rf.rfTypeCode < 500
AND u.userSkin = #{skin}
AND p.prdSubCate = #{sub_cate}
) AS cnt FROM PRODUCT p WHERE 1 = 1
AND p.prdSubCate = #{sub_cate}
ORDER BY cnt DESC FETCH FIRST #{limit} ROWS ONLY
