mapper.select:
SELECT COUNT(*)
FROM REVIEW
WHERE prdNo = #{prdNo}

SELECT AVG(revStar)
FROM REVIEW
WHERE prdNo = #{prdNo}

mapper.update:
UPDATE REVIEW
SET revStar = #{revStar},
    revGood = #{revGood},
    revBad  = #{revBad},
    revImg  = #{revImg}
WHERE revNo = #{revNo}

mapper.select:
SELECT revUDNo
FROM REVIEW_UPDOWN
WHERE revNo = #{revNo}
  AND userNo = #{userNo}

mapper.delete:
DELETE
FROM REVIEW_UPDOWN
WHERE revNo = #{revNo}
  AND userNo = #{userNo}

mapper.select:
SELECT COUNT(*)
FROM REVIEW_UPDOWN
WHERE revNo = #{revNo}

mapper.update:
UPDATE REVIEW
SET revTrustScore = #{revTrustScore},
    revTrustRank  = #{revTrustRank}
WHERE revNo = #{revNo}
